import React from 'react';
import ReactDOM from 'react-dom';
import Product from './Product';

class Products extends React.Component {
    constructor(props) {
        super(props)
        this.state = {products: []}
        this.getProducts()
    }

    getProducts() {
        fetch('http://localhost:8000/products').then(res => res.json()).then((result) => {
            
            let productsList = []
            for(let i=0; i < result.length; i++) {
              let product = result[i];
              productsList.push((<Product className='product' face={product.face} size={product.size} date={product.date} price={product.price}/>))
            }
            console.log(result[0])
            console.log(result[0].size)
            this.setState({products: productsList})
            console.log('inside getProducts')
            console.log(this.state.products)
          }).catch((error) => {
            console.log(error)
          })
    }
    
    render() {
        return (
            <div id='productGrid'>
            <div className='row'>
            {this.state.products}
            </div>
        </div>
        );
    }

}

export default Products;
