import React from 'react';
import ReactDOM from 'react-dom';

class Product extends React.Component {
    constructor(props) {
        super(props)
    }

    formatDate() {
        let currDate = new Date()
        console.log('here')
        console.log(currDate)
        console.log(currDate.getMonth() + '/' + currDate.getDate() + '/' + currDate.getFullYear())
        

        let productDate = new Date(this.props.date);
        //n console.log(this.props.date.getMonth() + '/' + this.props.date.getDate() + '/' + this.props.date.getFullYear())

        let dayDiff = Math.floor((currDate.getTime() - productDate.getTime()) / (1000 * 60 * 60 * 24));

        if(dayDiff < 7) {
            return dayDiff + (dayDiff === 1 ? " day ago" : " days ago")
        }
        else {
            return (productDate.getMonth() + 1) + '/' + productDate.getDate() + '/' + productDate.getFullYear() 
        }
    }
    
    render() {
        return (
            <div>
                <div>
                    {this.props.face}
                </div>
                <div>
                    {this.formatDate()}
                </div>
                <div>
                    {this.props.size} pixels
                    ${this.props.price / 100}
                </div>
            </div>
        );
    }

}

export default Product;
