import React from 'react';
import ReactDOM from 'react-dom';

class Product extends React.Component {
    constructor(props) {
        super(props)
        this.state = {currDate: newDate()}
    }

    formatDate() {
        let currDate = new Date()
        console.log('here')
        console.log(currDate)
        console.log(currDate.getDate())
        console.log(currDate - this.props.date)
        if(currDate - this.props.date < 7) {
            return currDate - this.props.date + " days ago"
        }
        else {
            return this.props.date 
        }
    }
    
    render() {
        return (
            <div>
                <div>
                    {this.props.face}
                </div>
                <div>
                    {this.formatDate()}
                </div>
                <div>
                    {this.props.size} pixels
                    ${this.props.price / 100}
                </div>
            </div>
        );
    }

}

export default Product;
